from django.urls import pathfrom .views import *urlpatterns = [    path('api/products/', HomeApiViews.as_view(), name='home'),    path('api/new/', CreateApiViews.as_view(), name='create-page'),    path('api/<id>/', DetailApiViews.as_view(), name='detail-page'),    path('api/<id>/update/', UpdateApiViews.as_view(), name='update'),    # Category    path('api/category/<product>/', CategoryProductsViews.as_view(), name='CAtegory'),    # Search    path('api/search?product=<title>/', SearchProductTitleViews.as_view(), name='Search Title'),    #    path('api/product/<id>/delete/', DeleteApiViews.as_view(), name='delete'),    path('<str:username>/', UserPostsApiViews.as_view(), name='users'),    path('api/user/<username>/', ForwardUsersViews.as_view(), name='username-page'),    path('api/user/<username>/update/', OnlyAuthUsersViews.as_view(), name='users profile update'),]